INSERT INTO `cosplay_posts` VALUES (452,1,'2013-03-30 15:19:10','2013-03-30 07:19:10','<h2><span style=\"color: #ff9900;\"><span style=\"color: #ff99cc;\">动漫！美景！温泉！购物！全方位一本满足！最有爱的九州之旅</span><span style=\"font-family: Times New Roman;\">~</span></span></h2>\r\n<h1> </h1>\r\n&nbsp;\r\n<h4><span style=\"color: #808080;\">第一日：</span></h4>\r\n<h4><span style=\"color: #808080;\">出发到长崎豪斯登堡</span></h4>\r\n<h4><span style=\"color: #808080;\"> </span></h4>\r\n<h4><span style=\"color: #808080;\">享受欧风小镇悠闲风光</span></h4>\r\n<h4><span style=\"color: #808080;\">浪漫的鲜花与灯光盛宴</span></h4>\r\n<h4><span style=\"color: #808080;\"> </span></h4>\r\n<h4><span style=\"color: #808080;\">第二日：</span></h4>\r\n<h4><span style=\"color: #808080;\">海贼王主题乐园</span></h4>\r\n<h4><span style=\"color: #808080;\">爱与梦想的千阳号出航</span></h4>\r\n<h4><span style=\"color: #808080;\"> </span></h4>\r\n<h4><span style=\"color: #808080;\">豪斯登堡出发到小仓</span></h4>\r\n<h4><span style=\"color: #808080;\"> </span></h4>\r\n<h4><span style=\"color: #808080;\">ARUARU CITY<span style=\"font-family: 宋体;\">动漫城</span><span style=\"font-family: Times New Roman;\">+</span><span style=\"font-family: 宋体;\">小仓城</span><span style=\"font-family: Times New Roman;\">+</span><span style=\"font-family: 宋体;\">门司港怀旧区</span></span></h4>\r\n<h4><span style=\"color: #808080;\">大型动漫综合商城 爱好者的体验购物天堂</span></h4>\r\n<h4><span style=\"color: #808080;\">小仓城 赏樱名所 历史爱好者必去的景点</span></h4>\r\n<h4><span style=\"color: #808080;\">大正浪漫风情的海港城市 </span></h4>\r\n<h4><span style=\"color: #808080;\"> </span></h4>\r\n<h4><span style=\"color: #808080;\">第三日：</span></h4>\r\n<h4><span style=\"color: #808080;\">小仓出发到温泉胜地别府</span></h4>\r\n<h4><span style=\"color: #808080;\"> </span></h4>\r\n<h4><span style=\"color: #808080;\">世界第一的温泉乡</span></h4>\r\n<h4><span style=\"color: #808080;\">游览独特的温泉地貌，露天温泉享受悠闲的时光</span></h4>\r\n<h4><span style=\"color: #808080;\"> </span></h4>\r\n<h4><span style=\"color: #808080;\">第四日：</span></h4>\r\n<h4><span style=\"color: #808080;\">小仓出发到福冈</span></h4>\r\n<h4><span style=\"color: #808080;\"> </span></h4>\r\n<h4><span style=\"color: #808080;\">日本九州最大的中心都市</span></h4>\r\n<h4><span style=\"color: #808080;\">太宰府观光<span style=\"font-family: Times New Roman;\">+</span><span style=\"font-family: 宋体;\">博多美食</span><span style=\"font-family: Times New Roman;\">+</span><span style=\"font-family: 宋体;\">运河城、天神商业街</span><span style=\"font-family: Times New Roman;\">+</span><span style=\"font-family: 宋体;\">中洲川端商店街</span></span></h4>\r\n<h4><span style=\"color: #808080;\"> </span></h4>\r\n<h4><span style=\"color: #808080;\">第五日：</span></h4>\r\n<h4><span style=\"color: #808080;\">福冈自由活动</span></h4>\r\n<h4><span style=\"color: #808080;\">机场免税店</span></h4>\r\n<h4><span style=\"color: #808080;\">回程</span></h4>','豪斯登堡+小仓+别府+福冈5日游','','inherit','open','open','','432-revision-3','','','2013-03-30 15:19:10','2013-03-30 07:19:10','',432,'http://happycoder.tk/?p=452',0,'revision','',0),(453,1,'2013-03-30 17:18:49','0000-00-00 00:00:00','','自动草稿','','auto-draft','open','open','','','','','2013-03-30 17:18:49','0000-00-00 00:00:00','',0,'http://happycoder.tk/?p=453',0,'post','',0),(454,1,'2013-03-30 21:27:28','2013-03-30 13:27:28','TEST','TEST','','publish','open','open','','test','','','2013-03-30 21:27:28','2013-03-30 13:27:28','',0,'http://happycoder.tk/?p=454',0,'post','',0),(455,1,'2013-03-30 21:26:52','2013-03-30 13:26:52','','6af9dedcgw1e36xshlsm6j','','inherit','open','open','','6af9dedcgw1e36xshlsm6j','','','2013-03-30 21:26:52','2013-03-30 13:26:52','',454,'http://happycoder.tk/wp-content/uploads/2013/03/6af9dedcgw1e36xshlsm6j.jpg',0,'attachment','image/jpeg',0),(456,1,'2013-03-30 21:26:34','2013-03-30 13:26:34','','TEST','','inherit','open','open','','454-revision','','','2013-03-30 21:26:34','2013-03-30 13:26:34','',454,'http://happycoder.tk/?p=456',0,'revision','',0),(457,1,'2013-04-02 09:15:54','0000-00-00 00:00:00','','自动草稿','','auto-draft','open','open','','','','','2013-04-02 09:15:54','0000-00-00 00:00:00','',0,'http://happycoder.tk/?p=457',0,'post','',0),(458,1,'2013-04-02 09:16:13','2013-04-02 01:16:13','','11','','inherit','open','open','','11-2','','','2013-04-02 09:16:13','2013-04-02 01:16:13','',457,'http://happycoder.tk/wp-content/uploads/2013/04/11.jpg',0,'attachment','image/jpeg',0),(459,1,'2013-04-02 09:35:24','2013-04-02 01:35:24','TEST','TEST','','inherit','open','open','','454-autosave','','','2013-04-02 09:35:24','2013-04-02 01:35:24','',454,'http://happycoder.tk/?p=459',0,'revision','',0);

